# Hypothetical Example: Demonstrating System Instability due to Infinite Loop and Resource Exhaustion

import time

def infinite_loop():
    """Simulates an infinite loop that consumes CPU resources."""
    while True:
        # Perform an expensive operation (e.g., calculating a large number of Fibonacci numbers)
        fib_num = fibonacci(10000)
        print(fib_num)

def fibonacci(n):
    """Calculates the nth Fibonacci number."""
    if n <= 0:
        return 0
    elif n == 1:
        return 1
    else:
        return fibonacci(n-1) + fibonacci(n-2)

def resource_exhaustion():
    """Simulates a situation where a function consumes all available memory."""
    # Initialize a large list to consume memory
    large_list = [i for i in range(1000000)]

    # Perform an expensive operation (e.g., sorting the list) that consumes more memory
    large_list.sort()

def main():
    # Simulate a situation where the system is left running with an infinite loop
    print("Starting infinite loop...")
    infinite_loop()

    # Simulate a situation where the system is left running with a resource exhaustion issue
    print("\nStarting resource exhaustion...")
    resource_exhaustion()

if __name__ == "__main__":
    main()

# Simulate a situation where the system is left running without any checks or limits
try:
    while True:
        main()
except KeyboardInterrupt:
    print("\nCaught KeyboardInterrupt, stopping simulation.")
```

